def maxBalancedShipments(weight):
    n = len(weight)
    count = 0
    i = 0

    while i < n - 1:
        cur_max = weight[i]
        j = i
        found = False

        while j < n:
            cur_max = max(cur_max, weight[j])
            if weight[j] < cur_max:
                # Valid shipment ends at j
                count += 1
                i = j + 1  # next shipment starts after j
                found = True
                break
            j += 1

        if not found:
            break

    return count



